select * from customer limit 10

' Q1. What is the total revenue generated by make vs. female customers ?'

select gender, sum(purchase_amount) as revenue from customer
group by gender

' 02. Which customers used a discount but still spent more rhan the average purchase amount?'
select customer_id , purchase_amount from customer 
where discount_applied = 'Yes' and purchase_amount >= (select avg(purchase_amount) from customer)

' 03. which are the top 5 products with the highest average review rating?'
select item_purchased, round(avg(review_rating),2) as 'average product rating' from customer
group by item_purchased
order by avg(review_rating) desc limit 5;

' 04. Compare the average purchase amounts between standard and express shipping.'
select shipping_type, avg(purchase_amount) from customer
where shipping_type in ('standard','express')
group by shipping_type

' 05. Do subscribed customers spend more? Compare average spend more and total revenue
between subscribers and non-subscribers?'
select subscription_status, count(customer_id) as total_customers,
avg(purchase_amount) as avg_spend,
sum(purchase_amount) as total_revenue from customer
group by subscription_status
order by total_revenue, avg_spend desc;

' 06. Which 5 products have the highest percentage of purchases with discounts applied?'
select item_purchased,
(sum(case when discount_applied = 'Yes' then 1 else 0 end)/count(*)) as discount_rate
from customer
group by item_purchased
order by discount_rate desc limit 5;

'07. Segment customers into New, Returning, and Loyal based on their total 
number of previos purchases, and show the count of each segment.'
with customer_type as (
select customer_id, previous_purchases,
case 
    when previous_purchases = 1 then 'New'
    when previous_purchases between 2 and 10 then 'Returning'
    else 'Loyal'
    end as customer_segment
from customer)

select customer_segment,count(*) AS "Number of Customers" 
from customer_type 
group by customer_segment;

' Q8. What are the top 3 most purchased products within each category?' 
with item_counts as (
    select category,
           item_purchased,
           count(customer_id) as total_orders,
           row_number() over (partition by category order by count(customer_id) desc) as item_rank
    from customer
    group by category, item_purchased
    )
select item_rank,category, item_purchased, total_orders
from item_counts
where item_rank <=3;
 
' Q9. Are customers who are repeat buyers (more than 5 previous purchases) also likely to subscribe?'
select subscription_status, count(customer_id) as repeat_buyers
from customer
where previous_purchases > 5
group by subscription_status;

' 10. What is the revenue contribution of each age group?' 
select age_group, sum(purchase_amount) as total_revenue
from customer
group by age_group
order by total_revenue desc;
